  // =====================================================
  // URL Query Parameter Encoding Tests
  // =====================================================

  describe('URL Query Parameter Encoding', () => {

    it('should handle Korean category in monthly-summary query', async () => {
      const categories = [
        'í˜‘ë ¥ì—…ì²´-ì›ìì¬',
        'í˜‘ë ¥ì—…ì²´-ì™¸ì£¼',
        'ì†Œëª¨í’ˆì—…ì²´',
