  });

  // =====================================================
  // Database Round-Trip Tests
  // =====================================================

  describe('Database Round-Trip Encoding', () => {

    it('should preserve Korean through full CRUD cycle', async () => {
      const testData = {
        company_code: `TEST-${Date.now()}`,
        company_name: 'íƒœì°½ì •ë°€ìë™ì°¨ë¶€í’ˆ(ì£¼)',
        company_type: 'ê³µê¸‰ì‚¬',
