      {/* Excel Upload Modal */}
      <ExcelUploadModal
        isOpen={showUploadModal}
        onClose={() => setShowUploadModal(false)}
        uploadUrl="/api/upload/companies"
        title="ê±°ë˜ì²˜ ì—‘ì…€ ì—…ë¡œë“œ"
        onUploadSuccess={handleUploadSuccess}
      />

      {/* Confirmation Dialog */}
      <ConfirmDialog />
    </div>
  );
}

