import { NextRequest, NextResponse } from 'next/server';
import { verifyToken, getUserFromToken, checkPermission } from '@/lib/auth';
import type { User, UserRole, JWTPayload } from '@/types/auth';
import { AUTH_ERRORS } from '@/types/auth';

// ìš”ì²­ì— ì‚¬ìš©ì ì •ë³´ë¥¼ ì¶”ê°€í•˜ê¸° ìœ„í•œ ì¸í„°í˜ì´ìŠ¤
export interface AuthenticatedRequest extends NextRequest {
  user?: User;
}

// ì¸ì¦ ë¯¸ë“¤ì›¨ì–´
export async function withAuth(request: NextRequest): Promise<NextResponse | { user: User }> {
  try {
    // Authorization í—¤ë”ì—ì„œ í† í° ì¶”ì¶œ
    const authHeader = request.headers.get('authorization');
    let token: string | null = null;

    if (authHeader && authHeader.startsWith('Bearer ')) {
      token = authHeader.substring(7);
    }

    // ì¿ í‚¤ì—ì„œ í† í° ì¶”ì¶œ (fallback)
    if (!token) {
      token = request.cookies.get('auth_token')?.value || null;
    }

    if (!token) {
      return NextResponse.json(
        { success: false, error: 'ì¸ì¦ í† í°ì´ ì—†ìŠµë‹ˆë‹¤.' },
        { status: 401 }
      );
    }

    // í† í° ê²€ì¦
    const userResult = await getUserFromToken(token);

    if ('code' in userResult) {
      // AuthErrorì¸ ê²½ìš°
      let status = 401;
      if (userResult.code === 'TOKEN_EXPIRED') {
        status = 401;
      } else if (userResult.code === 'ACCESS_DENIED') {
        status = 403;
      }

      return NextResponse.json(
        { success: false, error: userResult.message },
        { status }
      );
    }

    // ì •ìƒì ì¸ ì‚¬ìš©ì ê°ì²´ ë°˜í™˜
    return { user: userResult };

  } catch (error) {
    console.error('Authentication middleware error:', error);
    return NextResponse.json(
      { success: false, error: 'ì¸ì¦ ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.' },
      { status: 500 }
    );
  }
}

// ê¶Œí•œ í™•ì¸ ë¯¸ë“¤ì›¨ì–´
export function withRole(requiredRole: UserRole | UserRole[]) {
  return async (request: NextRequest, user: User): Promise<NextResponse | null> => {
    try {
      const allowedRoles = Array.isArray(requiredRole) ? requiredRole : [requiredRole];

      if (!allowedRoles.includes(user.role)) {
        return NextResponse.json(
          { success: false, error: 'ì ‘ê·¼ ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤.' },
          { status: 403 }
        );
      }

      return null; // ê¶Œí•œì´ ìˆìœ¼ë©´ null ë°˜í™˜ (í†µê³¼)

    } catch (error) {
      console.error('Role middleware error:', error);
      return NextResponse.json(
        { success: false, error: 'ê¶Œí•œ í™•ì¸ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.' },
        { status: 500 }
      );
    }
  };
}

// ë¦¬ì†ŒìŠ¤ë³„ ê¶Œí•œ í™•ì¸ ë¯¸ë“¤ì›¨ì–´
export function withPermission(resource: string, action: string) {
  return async (request: NextRequest, user: User): Promise<NextResponse | null> => {
    try {
      const hasAccess = checkPermission(user.role, resource, action);

      if (!hasAccess) {
        return NextResponse.json(
          { success: false, error: `${resource} ${action} ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤.` },
          { status: 403 }
        );
      }

      return null; // ê¶Œí•œì´ ìˆìœ¼ë©´ null ë°˜í™˜ (í†µê³¼)

    } catch (error) {
      console.error('Permission middleware error:', error);
      return NextResponse.json(
        { success: false, error: 'ê¶Œí•œ í™•ì¸ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.' },
        { status: 500 }
      );
    }
  };
}

// ë¯¸ë“¤ì›¨ì–´ ì²´ì¸ ì‹¤í–‰ í—¬í¼
export async function executeMiddleware(
  request: NextRequest,
  middlewares: Array<(req: NextRequest, user?: User) => Promise<NextResponse | { user: User } | null>>
): Promise<{ user?: User; response?: NextResponse }> {
  let user: User | undefined;

  for (const middleware of middlewares) {
    const result = await middleware(request, user);

    if (result === null) {
      // ë¯¸ë“¤ì›¨ì–´ í†µê³¼
      continue;
    }

    if ('user' in result) {
      // ì‚¬ìš©ì ì •ë³´ ì„¤ì •
      user = result.user;
      continue;
    }

    // NextResponseê°€ ë°˜í™˜ëœ ê²½ìš° (ì—ëŸ¬ ë˜ëŠ” ë¦¬ë””ë ‰íŠ¸)
    return { response: result };
  }

  return { user };
}

// API ë¼ìš°íŠ¸ ë³´í˜¸ ë˜í¼
export function protectRoute(
  handler: (request: NextRequest, user: User) => Promise<NextResponse>,
  options: {
    roles?: UserRole | UserRole[];
    resource?: string;
    action?: string;
  } = {}
) {
  return async (request: NextRequest): Promise<NextResponse> => {
    try {
      const middlewares: Array<(req: NextRequest, user?: User) => Promise<NextResponse | { user: User } | null>> = [withAuth];

      // ì—­í•  ê¸°ë°˜ ê¶Œí•œ ì²´í¬
      if (options.roles) {
        middlewares.push(async (req: NextRequest, user?: User) => {
          if (!user) return null;
          return await withRole(options.roles!)(req, user);
        });
      }

      // ë¦¬ì†ŒìŠ¤ë³„ ê¶Œí•œ ì²´í¬
      if (options.resource && options.action) {
        middlewares.push(async (req: NextRequest, user?: User) => {
          if (!user) return null;
          return await withPermission(options.resource!, options.action!)(req, user);
        });
      }

      const { user, response } = await executeMiddleware(request, middlewares);

      if (response) {
        return response;
      }

      if (!user) {
        return NextResponse.json(
          { success: false, error: 'ì¸ì¦ëœ ì‚¬ìš©ìê°€ ì•„ë‹™ë‹ˆë‹¤.' },
          { status: 401 }
        );
      }

      // ì‹¤ì œ í•¸ë“¤ëŸ¬ ì‹¤í–‰
      return await handler(request, user);

    } catch (error) {
      console.error('Protected route error:', error);
      return NextResponse.json(
        { success: false, error: 'ìš”ì²­ ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.' },
        { status: 500 }
      );
    }
  };
}

