import { NextRequest, NextResponse } from 'next/server';
import { query } from '@/lib/db-unified';

interface LowStockAlert {
  item_id: number;
  item_code: string;
  item_name: string;
  spec: string;
  unit: string;
  item_type: string;
  current_stock: number;
  safety_stock: number;
  reorder_point: number;
  severity: string;
  days_until_zero: number | null;
}

interface SummaryStats {
  total_alerts: number;
  critical_count: number;
  warning_count: number;
  critical_alerts: number;
  warning_alerts: number;
  avg_stock_ratio: number;
}

/**
 * GET /api/alerts/low-stock
 * Get low stock alerts
 * Query parameters:
 * - limit: Number of records to return (default: 50)
 * - offset: Pagination offset (default: 0)
 * - severity: Filter by severity (critical/warning)
 */
export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const limit = parseInt(searchParams.get('limit') || '50');
    const offset = parseInt(searchParams.get('offset') || '0');
    const severity = searchParams.get('severity');

    let sql = `
      SELECT
        i.item_id,
        i.item_code,
        i.item_name,
        i.spec,
        i.unit,
        i.item_type,
        COALESCE(
          (
            SELECT SUM(
              CASE
                WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                ELSE 0
              END
            )
            FROM inventory_transactions it
            WHERE it.item_id = i.item_id
          ), 0
        ) as current_stock,
        COALESCE(i.safety_stock, 0) as safety_stock,
        i.unit_price,
        CASE
          WHEN COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) = 0 THEN 'critical'
          WHEN COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) <= (COALESCE(i.safety_stock, 0) * 0.5) THEN 'critical'
          ELSE 'warning'
        END as severity,
        CASE
          WHEN COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) = 0 THEN 'ì¬ê³  ì—†ìŒ'
          WHEN COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) <= (COALESCE(i.safety_stock, 0) * 0.5) THEN 'ì¬ê³  ë¶€ì¡± (Critical)'
          ELSE 'ì¬ê³  ë¶€ì¡± (Warning)'
        END as alert_message,
        (
          SELECT MAX(it.transaction_date)
          FROM inventory_transactions it
          WHERE it.item_id = i.item_id
        ) as last_transaction_date
      FROM items i
      WHERE i.is_active = 1
      AND COALESCE(
        (
          SELECT SUM(
            CASE
              WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
              WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
              WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
              ELSE 0
            END
          )
          FROM inventory_transactions it
          WHERE it.item_id = i.item_id
        ), 0
      ) <= COALESCE(i.safety_stock, 0)
    `;

    const params: unknown[] = [];

    if (severity) {
      if (severity === 'critical') {
        sql += ` AND (
          COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) = 0
          OR
          COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) <= (COALESCE(i.safety_stock, 0) * 0.5)
        )`;
      } else if (severity === 'warning') {
        sql += ` AND COALESCE(
          (
            SELECT SUM(
              CASE
                WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                ELSE 0
              END
            )
            FROM inventory_transactions it
            WHERE it.item_id = i.item_id
          ), 0
        ) > (COALESCE(i.safety_stock, 0) * 0.5)
        AND COALESCE(
          (
            SELECT SUM(
              CASE
                WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                ELSE 0
              END
            )
            FROM inventory_transactions it
            WHERE it.item_id = i.item_id
          ), 0
        ) > 0`;
      }
    }

    sql += ' ORDER BY severity DESC, current_stock ASC, i.item_code';
    sql += ` LIMIT ${limit} OFFSET ${offset}`;

    const alerts = await query<LowStockAlert[]>(sql, params);

    // Get summary statistics
    const summaryQuery = `
      SELECT
        COUNT(*) as total_alerts,
        SUM(
          CASE
            WHEN COALESCE(
              (
                SELECT SUM(
                  CASE
                    WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                    WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                    WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                    ELSE 0
                  END
                )
                FROM inventory_transactions it
                WHERE it.item_id = i.item_id
              ), 0
            ) = 0 THEN 1
            WHEN COALESCE(
              (
                SELECT SUM(
                  CASE
                    WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                    WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                    WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                    ELSE 0
                  END
                )
                FROM inventory_transactions it
                WHERE it.item_id = i.item_id
              ), 0
            ) <= (COALESCE(i.safety_stock, 0) * 0.5) THEN 1
            ELSE 0
          END
        ) as critical_alerts,
        SUM(
          CASE
            WHEN COALESCE(
              (
                SELECT SUM(
                  CASE
                    WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                    WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                    WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                    ELSE 0
                  END
                )
                FROM inventory_transactions it
                WHERE it.item_id = i.item_id
              ), 0
            ) > (COALESCE(i.safety_stock, 0) * 0.5)
            AND COALESCE(
              (
                SELECT SUM(
                  CASE
                    WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                    WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                    WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                    ELSE 0
                  END
                )
                FROM inventory_transactions it
                WHERE it.item_id = i.item_id
              ), 0
            ) <= COALESCE(i.safety_stock, 0)
            AND COALESCE(
              (
                SELECT SUM(
                  CASE
                    WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                    WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                    WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                    ELSE 0
                  END
                )
                FROM inventory_transactions it
                WHERE it.item_id = i.item_id
              ), 0
            ) > 0 THEN 1
            ELSE 0
          END
        ) as warning_alerts
      FROM items i
      WHERE i.is_active = 1
      AND COALESCE(
        (
          SELECT SUM(
            CASE
              WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
              WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
              WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
              ELSE 0
            END
          )
          FROM inventory_transactions it
          WHERE it.item_id = i.item_id
        ), 0
      ) <= COALESCE(i.safety_stock, 0)
    `;

    const summaryResult = await query<SummaryStats>(summaryQuery);
    const summary = summaryResult[0] || { total_alerts: 0, critical_alerts: 0, warning_alerts: 0, critical_count: 0, warning_count: 0, avg_stock_ratio: 0 };

    // Get total count for pagination
    let countSql = `
      SELECT COUNT(*) as total
      FROM items i
      WHERE i.is_active = 1
      AND COALESCE(
        (
          SELECT SUM(
            CASE
              WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
              WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
              WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
              ELSE 0
            END
          )
          FROM inventory_transactions it
          WHERE it.item_id = i.item_id
        ), 0
      ) <= COALESCE(i.safety_stock, 0)
    `;
    const countParams: (string | number)[] = [];

    if (severity) {
      if (severity === 'critical') {
        countSql += ` AND (
          COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) = 0
          OR
          COALESCE(
            (
              SELECT SUM(
                CASE
                  WHEN it.transaction_type = 'ì…ê³ ' THEN it.quantity
                  WHEN it.transaction_type = 'ì¶œê³ ' THEN -it.quantity
                  WHEN it.transaction_type = 'ì¡°ì •' THEN it.quantity
                  ELSE 0
                END
              )
              FROM inventory_transactions it
              WHERE it.item_id = i.item_id
            ), 0
          ) <= (COALESCE(i.safety_stock, 0) * 0.5)
        )`;
      }
    }

    const countQueryResult = await query<{ total: number }>(countSql, countParams);
    const countResult = countQueryResult[0] || { total: 0 };

    return NextResponse.json({
      success: true,
      data: {
        alerts,
        summary: {
          total_alerts: summary.total_alerts || 0,
          critical_alerts: summary.critical_alerts || 0,
          warning_alerts: summary.warning_alerts || 0
        },
        pagination: {
          total: countResult.total,
          limit,
          offset,
          hasMore: offset + limit < countResult.total
        }
      }
    });
  } catch (error) {
    console.error('Error fetching low stock alerts:', error);
    return NextResponse.json(
      {
        success: false,
        error: 'ì¬ê³  ë¶€ì¡± ì•Œë¦¼ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.'
      },
      { status: 500 }
    );
  }
}

src/app/api/stock/reports/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { getSupabaseClient } from '@/lib/db-unified';

export async function GET(request: NextRequest): Promise<NextResponse> {
  try {
    const { searchParams } = new URL(request.url);
    const reportDate = searchParams.get('date') || new Date().toISOString().split('T')[0];

    const supabase = getSupabaseClient();

    // Get items with stock information
    const { data: itemsData, error: itemsError } = await supabase
      .from('items')
      .select('*')
      .eq('is_active', true);

    if (itemsError) throw itemsError;

    // Calculate stock data for each item
    const stockData = (itemsData || []).map((item: any) => {
      const currentStock = item.current_stock || 0;
      const safetyStock = item.safety_stock || 0;
      const stockValue = (item.price || 0) * currentStock;
      const isLowStock = currentStock <= safetyStock;

      return {
        item_id: item.item_id,
        item_code: item.item_code,
        item_name: item.item_name,
        spec: item.spec,
        category: item.category,
        unit: item.unit,
        current_stock: currentStock,
        safety_stock: safetyStock,
        stock_value: stockValue,
        is_low_stock: isLowStock,
        price: item.price || 0
      };
    });

    // Calculate summary statistics
    const totalItems = stockData.length;
    const totalStockValue = stockData.reduce((sum, item) => sum + item.stock_value, 0);
    const lowStockItems = stockData.filter(item => item.is_low_stock).length;
    const outOfStockItems = stockData.filter(item => item.current_stock === 0).length;

    // Calculate excess stock items (stock > 2x safety stock)
    const excessStockItems = stockData.filter(item => 
      item.current_stock > (item.safety_stock * 2)
    ).length;

    // Category breakdown
    const categoryMap = new Map<string, { count: number; value: number }>();
    stockData.forEach(item => {
      const category = item.category || 'ê¸°íƒ€';
      const existing = categoryMap.get(category) || { count: 0, value: 0 };
      categoryMap.set(category, {
        count: existing.count + 1,
        value: existing.value + item.stock_value
      });
    });

    const categoryBreakdown = Array.from(categoryMap.entries()).map(([category, data]) => ({
      item_type: category,
      item_count: data.count,
      stock_value: data.value,
      percentage: totalStockValue > 0 ? (data.value / totalStockValue) * 100 : 0
    }));

    // Top items by value
    const topValueItems = [...stockData]
      .sort((a, b) => b.stock_value - a.stock_value)
      .slice(0, 10)
      .map(item => ({
        item_code: item.item_code,
        item_name: item.item_name,
        stock_value: item.stock_value,
        current_stock: item.current_stock,
        safety_stock: item.safety_stock
      }));

    // Low stock items
    const lowStockItemsList = stockData
      .filter(item => item.is_low_stock)
      .map(item => ({
        item_code: item.item_code,
        item_name: item.item_name,
        stock_value: item.stock_value,
        current_stock: item.current_stock,
        safety_stock: item.safety_stock
      }));

    // Monthly trend (last 6 months)
    const sixMonthsAgo = new Date();
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

    const { data: transactionData, error: transactionError } = await (supabase as any)
      .from('inventory_transactions')
      .select('*')
      .gte('transaction_date', sixMonthsAgo.toISOString().split('T')[0]);

    if (transactionError) {
      console.error('Error fetching transactions:', transactionError);
    }

    // Group transactions by month
    const monthlyMap = new Map<string, {
      stock_value: number;
      transaction_count: number;
      in_quantity: number;
      out_quantity: number;
    }>();

    (transactionData || []).forEach((tx: any) => {
      const month = tx.transaction_date?.substring(0, 7) || '';
      if (!month) return;

      const existing = monthlyMap.get(month) || {
        stock_value: 0,
        transaction_count: 0,
        in_quantity: 0,
        out_quantity: 0
      };

      monthlyMap.set(month, {
        stock_value: existing.stock_value + (tx.total_amount || 0),
        transaction_count: existing.transaction_count + 1,
        in_quantity: existing.in_quantity + (tx.transaction_type === 'ì…ê³ ' ? tx.quantity : 0),
        out_quantity: existing.out_quantity + (tx.transaction_type === 'ì¶œê³ ' ? tx.quantity : 0)
      });
    });

    const monthlyTrend = Array.from(monthlyMap.entries())
      .map(([month, data]) => ({
        month,
        ...data
      }))
      .sort((a, b) => a.month.localeCompare(b.month));

    return NextResponse.json({
      success: true,
      data: {
        summary: {
          total_items: totalItems,
          total_stock_value: totalStockValue,
          low_stock_items: lowStockItems,
          excess_stock_items: excessStockItems,
          out_of_stock_items: outOfStockItems
        },
        categoryBreakdown,
        monthlyTrend,
        topValueItems,
        lowStockItems: lowStockItemsList,
        report_date: reportDate,
        generated_at: new Date().toISOString()
      }
    });
  } catch (error) {
    console.error('Error generating stock report:', error);
    return NextResponse.json(
      {
        success: false,
        error: 'ì¬ê³  ë³´ê³ ì„œ ìƒì„± ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.',
        details: error instanceof Error ? error.message : 'Unknown error'
      },
      { status: 500 }
    );
  }
}

