/**
 * Transaction Distribution Chart Component
 * Displays transaction type distribution with interactive pie and donut charts
 */

import React, { useState, useRef, useMemo } from 'react';
import {
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer,
  Tooltip,
  Legend,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid
} from 'recharts';
import { PieChart as PieChartIcon, BarChart3, Download, Printer, RefreshCcw, Activity } from 'lucide-react';
import {
  formatKoreanNumber,
  getRechartsTheme,
  exportChartAsImage,
  printChart,
  debounce,
  getTransactionTypeColor
} from '../../utils/chartUtils';

interface TransactionDistributionData {
  type: string;
  count: number;
  volume: number;
  value: number;
  percentage: number;
  items: number;
  avgPerTransaction: number;
  companies: number;
}

interface TransactionDistributionProps {
  data: TransactionDistributionData[] | null;
  loading: boolean;
  error: string | null;
  isDark?: boolean;
  onRefresh?: () => void;
  showControls?: boolean;
  className?: string;
  onTypeClick?: (type: string) => void;
}

type ChartType = 'pie' | 'donut' | 'bar';
type MetricType = 'count' | 'volume' | 'value';
type TimeRange = 'today' | 'week' | 'month' | 'quarter';

export const TransactionDistribution: React.FC<TransactionDistributionProps> = ({
  data,
  loading,
  error,
  isDark = false,
  onRefresh,
  showControls = true,
  className = '',
  onTypeClick
}) => {
  const [chartType, setChartType] = useState<ChartType>('donut');
  const [selectedMetric, setSelectedMetric] = useState<MetricType>('volume');
  const [timeRange, setTimeRange] = useState<TimeRange>('month');
  const [showLabels, setShowLabels] = useState(true);
  const [showLegend, setShowLegend] = useState(true);
  const [selectedTypes, setSelectedTypes] = useState<Set<string>>(new Set());

  const chartRef = useRef<any>(null);
  const theme = getRechartsTheme(isDark);

  // Process data for display
  const processedData = useMemo(() => {
    if (!data) return [];

    return data.map((item, index) => ({
      ...item,
      displayValue: selectedMetric === 'count' ? item.count
                   : selectedMetric === 'volume' ? item.volume
                   : item.value,
      color: getTransactionTypeColor(item.type, isDark),
      id: `${item.type}-${index}`
    })).sort((a, b) => b.displayValue - a.displayValue);
  }, [data, selectedMetric, isDark]);

  // Calculate total and percentages
  const totals = useMemo(() => {
    if (!processedData.length) return null;

    const total = processedData.reduce((sum, item) => sum + item.displayValue, 0);
    const totalCount = processedData.reduce((sum, item) => sum + item.count, 0);
    const totalVolume = processedData.reduce((sum, item) => sum + item.volume, 0);
    const totalValue = processedData.reduce((sum, item) => sum + item.value, 0);

    return {
      total,
      totalCount,
      totalVolume,
      totalValue,
      avgTransaction: totalCount > 0 ? totalVolume / totalCount : 0,
      topType: processedData[0]?.type || '',
      topPercentage: total > 0 ? (processedData[0]?.displayValue || 0) / total * 100 : 0
    };
  }, [processedData]);

  // Custom tooltip for pie charts
  const PieTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0];
      const percentage = totals && totals.total > 0 ? (data.value / totals.total * 100) : 0;

      return (
        <div
          className="bg-white dark:bg-gray-800 p-4 border border-gray-200 dark:border-gray-600 rounded-lg shadow-lg min-w-[200px]"
          style={theme.tooltip.contentStyle}
        >
          <div className="flex items-center space-x-2 mb-3">
            <span
              className="w-4 h-4 rounded-full"
              style={{ backgroundColor: data.payload.color }}
            />
            <p className="font-semibold text-gray-900 dark:text-gray-100">
              {data.payload.type}
            </p>
          </div>

          <div className="space-y-2">
            <div className="flex justify-between items-center">
              <span className="text-gray-600 dark:text-gray-400 text-sm">ê±°ë˜ ê±´ìˆ˜:</span>
              <span className="font-medium text-gray-900 dark:text-gray-100">
                {formatKoreanNumber(data.payload.count)}ê±´
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600 dark:text-gray-400 text-sm">ê±°ë˜ëŸ‰:</span>
              <span className="font-medium text-gray-900 dark:text-gray-100">
                {formatKoreanNumber(data.payload.volume)}ê°œ
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600 dark:text-gray-400 text-sm">ê±°ë˜ê¸ˆì•¡:</span>
              <span className="font-medium text-gray-900 dark:text-gray-100">
                â‚©{formatKoreanNumber(data.payload.value)}
              </span>
            </div>
            <div className="flex justify-between items-center pt-2 border-t border-gray-200 dark:border-gray-600">
              <span className="text-gray-600 dark:text-gray-400 text-sm">ë¹„ìœ¨:</span>
              <span className="font-medium text-blue-600">
                {percentage.toFixed(1)}%
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600 dark:text-gray-400 text-sm">í‰ê·  ê±°ë˜ëŸ‰:</span>
              <span className="font-medium text-gray-900 dark:text-gray-100">
                {formatKoreanNumber(data.payload.avgPerTransaction)}ê°œ
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600 dark:text-gray-400 text-sm">ê±°ë˜ì²˜ ìˆ˜:</span>
              <span className="font-medium text-gray-900 dark:text-gray-100">
                {data.payload.companies}ê°œ
              </span>
            </div>
          </div>
        </div>
      );
    }
    return null;
  };

  // Custom label function
  const renderCustomLabel = ({
    cx, cy, midAngle, innerRadius, outerRadius, percent, name
  }: any) => {
    if (!showLabels || percent < 0.05) return null; // Don't show labels for slices < 5%

    const RADIAN = Math.PI / 180;
    const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
    const x = cx + radius * Math.cos(-midAngle * RADIAN);
    const y = cy + radius * Math.sin(-midAngle * RADIAN);

    return (
      <text
        x={x}
        y={y}
        fill={isDark ? '#F9FAFB' : '#1F2937'}
        textAnchor={x > cx ? 'start' : 'end'}
        dominantBaseline="central"
        fontSize="12"
        fontWeight="500"
      >
        {`${name} ${(percent * 100).toFixed(1)}%`}
      </text>
    );
  };

  // Handle sector click
  const handleSectorClick = (data: any) => {
    if (onTypeClick) {
      onTypeClick(data.type);
    }

    const newSelected = new Set(selectedTypes);
    if (newSelected.has(data.type)) {
      newSelected.delete(data.type);
    } else {
      newSelected.add(data.type);
    }
    setSelectedTypes(newSelected);
  };

  // Handle refresh with debounce
  const debouncedRefresh = debounce(() => {
    onRefresh?.();
  }, 1000);

  // Get metric label
  const getMetricLabel = () => {
    switch (selectedMetric) {
      case 'count': return 'ê±°ë˜ ê±´ìˆ˜';
      case 'volume': return 'ê±°ë˜ëŸ‰';
      case 'value': return 'ê±°ë˜ê¸ˆì•¡';
      default: return 'ê±°ë˜ëŸ‰';
    }
  };

  if (error) {
    return (
      <div className={`bg-white dark:bg-gray-900 rounded-lg p-6 shadow-sm ${className}`}>
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
            ê±°ë˜ ìœ í˜• ë¶„í¬
          </h3>
        </div>
        <div className="flex items-center justify-center h-64 text-red-500">
          <div className="text-center">
            <PieChartIcon className="w-12 h-12 mx-auto mb-2 opacity-50" />
            <p>ì°¨íŠ¸ ë°ì´í„° ë¡œë“œ ì‹¤íŒ¨</p>
            <p className="text-sm text-gray-500 mt-1">{error}</p>
            {onRefresh && (
              <button
                onClick={debouncedRefresh}
                className="mt-3 px-4 py-2 bg-red-100 hover:bg-red-200 dark:bg-red-900 dark:hover:bg-red-800 text-red-800 dark:text-red-300 rounded-lg font-medium transition-colors"
              >
                ë‹¤ì‹œ ì‹œë„
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`bg-white dark:bg-gray-900 rounded-lg p-6 shadow-sm ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-2">
          <Activity className="w-5 h-5 text-purple-500" />
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
            ê±°ë˜ ìœ í˜• ë¶„í¬
          </h3>
          {totals && (
            <span className="ml-2 px-2 py-1 bg-gray-100 dark:bg-gray-700 text-gray-600 dark:text-gray-400 text-xs rounded-lg">
              ì´ {formatKoreanNumber(totals.totalCount)}ê±´
            </span>
          )}
        </div>

        {/* Controls */}
        {showControls && (
          <div className="flex items-center space-x-2">
            {/* Time Range */}
            <select
              value={timeRange}
              onChange={(e) => setTimeRange(e.target.value as TimeRange)}
              className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 text-sm"
            >
              <option value="today">ì˜¤ëŠ˜</option>
              <option value="week">ì´ë²ˆ ì£¼</option>
              <option value="month">ì´ë²ˆ ë‹¬</option>
              <option value="quarter">ì´ë²ˆ ë¶„ê¸°</option>
            </select>

            {/* Metric Type */}
            <select
              value={selectedMetric}
              onChange={(e) => setSelectedMetric(e.target.value as MetricType)}
              className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 text-sm"
            >
              <option value="count">ê±´ìˆ˜</option>
              <option value="volume">ìˆ˜ëŸ‰</option>
              <option value="value">ê¸ˆì•¡</option>
            </select>

            {/* Chart Type */}
            <div className="flex items-center bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
              <button
                onClick={() => setChartType('pie')}
                className={`px-3 py-1 rounded text-sm font-medium transition-colors ${
                  chartType === 'pie'
                    ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400'
                }`}
              >
                íŒŒì´
              </button>
              <button
                onClick={() => setChartType('donut')}
                className={`px-3 py-1 rounded text-sm font-medium transition-colors ${
                  chartType === 'donut'
                    ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400'
                }`}
              >
                ë„ë„›
              </button>
              <button
                onClick={() => setChartType('bar')}
                className={`px-3 py-1 rounded text-sm font-medium transition-colors ${
                  chartType === 'bar'
                    ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow-sm'
                    : 'text-gray-600 dark:text-gray-400'
                }`}
              >
                ë§‰ëŒ€
              </button>
            </div>

            {/* Options */}
            {chartType !== 'bar' && (
              <>
                <button
                  onClick={() => setShowLabels(!showLabels)}
                  className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                    showLabels
                      ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300'
                      : 'bg-gray-100 text-gray-600 dark:bg-gray-700 dark:text-gray-400'
                  }`}
                >
                  ë ˆì´ë¸”
                </button>

                <button
                  onClick={() => setShowLegend(!showLegend)}
                  className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                    showLegend
                      ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300'
                      : 'bg-gray-100 text-gray-600 dark:bg-gray-700 dark:text-gray-400'
                  }`}
                >
                  ë²”ë¡€
                </button>
              </>
            )}

            {/* Refresh Button */}
            {onRefresh && (
              <button
                onClick={debouncedRefresh}
                disabled={loading}
                className="p-2 text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200 disabled:opacity-50"
                title="ë°ì´í„° ìƒˆë¡œê³ ì¹¨"
              >
                <RefreshCcw className={`w-4 h-4 ${loading ? 'animate-spin' : ''}`} />
              </button>
            )}

            {/* Export buttons */}
            <button
              onClick={() => exportChartAsImage(chartRef, 'ê±°ë˜ìœ í˜•ë¶„í¬.png')}
              className="p-2 text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
              title="ì´ë¯¸ì§€ë¡œ ë‚´ë³´ë‚´ê¸°"
            >
              <Download className="w-4 h-4" />
            </button>

            <button
              onClick={() => printChart(chartRef)}
              className="p-2 text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
              title="ì¸ì‡„"
            >
              <Printer className="w-4 h-4" />
            </button>
          </div>
        )}
      </div>

      {/* Chart */}
      <div className="h-96" ref={chartRef}>
        {loading ? (
          <div className="flex items-center justify-center h-full">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-500"></div>
          </div>
        ) : !processedData.length ? (
          <div className="flex items-center justify-center h-full text-gray-500">
            <div className="text-center">
              <PieChartIcon className="w-12 h-12 mx-auto mb-2 opacity-50" />
              <p>í‘œì‹œí•  ê±°ë˜ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤</p>
            </div>
          </div>
        ) : chartType === 'bar' ? (
          <ResponsiveContainer width="100%" height="100%">
            <BarChart data={processedData} margin={{ top: 20, right: 30, left: 20, bottom: 60 }}>
              <CartesianGrid strokeDasharray="3 3" stroke={theme.cartesianGrid.stroke} />
              <XAxis
                dataKey="type"
