import { NextRequest, NextResponse } from 'next/server';
import { supabaseAdmin } from '@/lib/supabase';

/**
 * POST /api/stock/adjustment
 * Create stock adjustment transaction
 * Body: {
 *   item_id: number,
 *   adjustment_type: 'INCREASE' | 'DECREASE' | 'SET',
 *   quantity: number,
