 * }
 */
export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const {
      item_id,
      adjustment_type,
      quantity,
      reason,
      reference_no,
      notes,
      created_by
    } = body;

    // í•„ìˆ˜ í•„ë“œ ê²€ì¦
    if (!item_id || !adjustment_type || quantity === undefined || !reason || !created_by) {
      return NextResponse.json({
        success: false,
        error: 'í•„ìˆ˜ í•„ë“œê°€ ëˆ„ë½ë˜ì—ˆìŠµë‹ˆë‹¤. (í’ˆëª©, ì¡°ì •ìœ í˜•, ìˆ˜ëŸ‰, ì‚¬ìœ , ì‘ì„±ì í•„ìˆ˜)'
      }, { status: 400 });
    }

    if (quantity === 0) {
      return NextResponse.json({
        success: false,
        error: 'ì¡°ì • ìˆ˜ëŸ‰ì€ 0ì´ ë  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
      }, { status: 400 });
    }

    // ì¡°ì • ìœ í˜• ê²€ì¦
    if (!['INCREASE', 'DECREASE', 'SET'].includes(adjustment_type)) {
      return NextResponse.json({
        success: false,
        error: 'ì˜¬ë°”ë¥´ì§€ ì•Šì€ ì¡°ì • ìœ í˜•ì…ë‹ˆë‹¤.'
      }, { status: 400 });
    }

    // Check if item exists and is active
    const { data: itemCheck, error: itemError } = await supabaseAdmin
      .from('items')
      .select('item_id, item_name, unit, is_active')
      .eq('item_id', item_id)
      .single();

    if (itemError || !itemCheck) {
      return NextResponse.json({
        success: false,
        error: 'ì¡´ì¬í•˜ì§€ ì•ŠëŠ” í’ˆëª©ì…ë‹ˆë‹¤.'
      }, { status: 404 });
    }

    if (!itemCheck.is_active) {
      return NextResponse.json({
        success: false,
        error: 'ë¹„í™œì„±í™”ëœ í’ˆëª©ì…ë‹ˆë‹¤.'
      }, { status: 400 });
    }

    // Calculate current stock manually (RPC not used for stock calculation)
    const { data: transactions, error: txError } = await supabaseAdmin
      .from('inventory_transactions')
      .select('transaction_type, quantity')
      .eq('item_id', item_id);

    if (txError) {
      throw new Error(`ì¬ê³  ì¡°íšŒ ì‹¤íŒ¨: ${txError.message}`);
    }

    const finalCurrentStock = (transactions || []).reduce((sum, tx) => {
      if (tx.transaction_type === 'ì…ê³ ') return sum + tx.quantity;
      if (tx.transaction_type === 'ì¶œê³ ') return sum - tx.quantity;
      if (tx.transaction_type === 'ì¡°ì •') return sum + tx.quantity;
      return sum;
    }, 0);

    // Calculate adjustment quantity based on type
    let adjustmentQuantity: number;
    let newStock: number;

    switch (adjustment_type) {
      case 'INCREASE':
        adjustmentQuantity = Math.abs(quantity);
        newStock = finalCurrentStock + adjustmentQuantity;
        break;
      case 'DECREASE':
        adjustmentQuantity = -Math.abs(quantity);
        newStock = finalCurrentStock + adjustmentQuantity;
        // Check if new stock would be negative
        if (newStock < 0) {
          return NextResponse.json({
            success: false,
            error: `ì¬ê³ ê°€ ë¶€ì¡±í•©ë‹ˆë‹¤. í˜„ì¬ ì¬ê³ : ${finalCurrentStock}, ìš”ì²­ ê°ì†ŒëŸ‰: ${Math.abs(quantity)}`
          }, { status: 400 });
        }
        break;
      case 'SET':
        adjustmentQuantity = quantity - finalCurrentStock;
        newStock = quantity;
        if (newStock < 0) {
          return NextResponse.json({
            success: false,
            error: 'ì¬ê³ ëŠ” ìŒìˆ˜ê°€ ë  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
          }, { status: 400 });
        }
        break;
      default:
        return NextResponse.json({
          success: false,
          error: 'ì˜¬ë°”ë¥´ì§€ ì•Šì€ ì¡°ì • ìœ í˜•ì…ë‹ˆë‹¤.'
        }, { status: 400 });
    }

    // Insert adjustment transaction
    const { data: insertedTransaction, error: insertError } = await supabaseAdmin
      .from('inventory_transactions')
      .insert({
        transaction_date: new Date().toISOString().split('T')[0],
        transaction_type: 'ì¡°ì •',
        item_id,
        quantity: adjustmentQuantity,
        unit_price: 0,
        total_amount: 0,
        reference_number: reference_no || `ADJ-${Date.now()}`,
        notes: `${reason}${notes ? ` | ${notes}` : ''} | ì´ì „ ì¬ê³ : ${finalCurrentStock} â†’ ì¡°ì • í›„: ${newStock}`,
        created_by
      })
      .select()
      .single();

    if (insertError) {
      console.error('[STOCK_ADJUSTMENT] Insert error:', insertError);
      throw new Error(`ê±°ë˜ ìƒì„± ì‹¤íŒ¨: ${insertError.message}`);
    }

    // Get full transaction details with joins
    const { data: fullTransaction, error: detailError } = await supabaseAdmin
      .from('inventory_transactions')
      .select(`
        *,
        items (item_code, item_name, spec, unit),
        users!inventory_transactions_created_by_fkey (name)
      `)
      .eq('transaction_id', insertedTransaction.transaction_id)
      .single();

    if (detailError) {
      console.error('[STOCK_ADJUSTMENT] Detail fetch error:', detailError);
    }

    const result = {
      ...insertedTransaction,
      item_code: fullTransaction?.items?.item_code,
      item_name: fullTransaction?.items?.item_name,
      specification: fullTransaction?.items?.spec,
      item_unit: fullTransaction?.items?.unit,
      created_by_name: fullTransaction?.users?.name,
      adjustment_type,
      current_stock_before: finalCurrentStock,
      current_stock_after: newStock
    };

    return NextResponse.json({
      success: true,
      message: 'ì¬ê³  ì¡°ì •ì´ ì„±ê³µì ìœ¼ë¡œ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤.',
      data: result
    });
  } catch (error) {
    console.error('Error creating stock adjustment:', error);
    const errorMessage = error instanceof Error ? error.message : 'ì¬ê³  ì¡°ì • ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.';
    return NextResponse.json(
      {
        success: false,
        error: errorMessage
      },
      { status: 500 }
    );
  }
}

/**
 * GET /api/stock/adjustment
 * Get stock adjustment history
 * Query parameters:
 * - start_date: Filter by start date (YYYY-MM-DD)
 * - end_date: Filter by end date (YYYY-MM-DD)
 * - item_id: Filter by specific item
 * - limit: Number of records to return (default: 100)
 * - offset: Pagination offset (default: 0)
 */
export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const startDate = searchParams.get('start_date');
    const endDate = searchParams.get('end_date');
    const itemId = searchParams.get('item_id');
    const limit = parseInt(searchParams.get('limit') || '100');
    const offset = parseInt(searchParams.get('offset') || '0');

    let query = supabaseAdmin
      .from('inventory_transactions')
      .select(`
        *,
        items (item_code, item_name, spec, unit),
        users!inventory_transactions_created_by_fkey (name)
      `, { count: 'exact' })
      .eq('transaction_type', 'ì¡°ì •')
      .order('transaction_date', { ascending: false })
      .order('created_at', { ascending: false })
      .range(offset, offset + limit - 1);

    if (startDate) {
      query = query.gte('transaction_date', startDate);
    }

    if (endDate) {
      query = query.lte('transaction_date', endDate);
    }

    if (itemId) {
      query = query.eq('item_id', parseInt(itemId));
    }

    const { data: adjustments, error, count } = await query;

    if (error) {
      throw new Error(error.message);
    }

    return NextResponse.json({
      success: true,
      data: {
        adjustments: adjustments || [],
        pagination: {
          total: count || 0,
          limit,
          offset,
          hasMore: offset + limit < (count || 0)
        }
      }
    });
  } catch (error) {
    console.error('Error fetching stock adjustments:', error);
    return NextResponse.json(
      {
        success: false,
        error: 'ì¬ê³  ì¡°ì • ì´ë ¥ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.'
      },
      { status: 500 }
    );
  }
}

