/**
 * ìƒíƒœ ê¸°ë°˜ í•„í„° ì»´í¬ë„ŒíŠ¸
 * Status-based Filter Component with visual indicators
 */

import React from 'react';
import { CheckCircle, AlertCircle, Clock, XCircle, Package } from 'lucide-react';
import { CategoryFilter, FilterOption } from './CategoryFilter';

export interface StatusOption extends FilterOption {
  color?: 'green' | 'yellow' | 'red' | 'blue' | 'gray';
  icon?: 'check' | 'alert' | 'clock' | 'x' | 'package';
  description?: string;
}

interface StatusFilterProps {
  options: StatusOption[];
  value?: string | string[];
  onChange: (value: string | string[]) => void;
  label?: string;
  placeholder?: string;
  multiple?: boolean;
  className?: string;
  disabled?: boolean;
  showCount?: boolean;
  allowClear?: boolean;
  showVisualIndicators?: boolean;
}

const StatusIcon: React.FC<{ type?: string; className?: string }> = ({ type, className = 'w-4 h-4' }) => {
  switch (type) {
    case 'check':
      return <CheckCircle className={className} />;
    case 'alert':
      return <AlertCircle className={className} />;
    case 'clock':
      return <Clock className={className} />;
    case 'x':
      return <XCircle className={className} />;
    case 'package':
      return <Package className={className} />;
    default:
      return <CheckCircle className={className} />;
  }
};

const getColorClasses = (color?: string) => {
  switch (color) {
    case 'green':
      return {
        bg: 'bg-green-100 dark:bg-green-900/20',
        text: 'text-green-800 dark:text-green-300',
        icon: 'text-green-600 dark:text-green-400'
      };
    case 'yellow':
      return {
        bg: 'bg-yellow-100 dark:bg-yellow-900/20',
        text: 'text-yellow-800 dark:text-yellow-300',
        icon: 'text-yellow-600 dark:text-yellow-400'
      };
    case 'red':
      return {
        bg: 'bg-red-100 dark:bg-red-900/20',
        text: 'text-red-800 dark:text-red-300',
        icon: 'text-red-600 dark:text-red-400'
      };
    case 'blue':
      return {
        bg: 'bg-blue-100 dark:bg-blue-900/20',
        text: 'text-blue-800 dark:text-blue-300',
        icon: 'text-blue-600 dark:text-blue-400'
      };
    default:
      return {
        bg: 'bg-gray-100 dark:bg-gray-700',
        text: 'text-gray-800 dark:text-gray-300',
        icon: 'text-gray-600 dark:text-gray-400'
      };
  }
};

export const StatusFilter: React.FC<StatusFilterProps> = ({
  options,
  value,
  onChange,
  label = 'ìƒíƒœ',
  placeholder = 'ìƒíƒœ ì„ íƒ',
  multiple = false,
  className = '',
  disabled = false,
  showCount = true,
  allowClear = true,
  showVisualIndicators = true
}) => {
  // ì‹œê°ì  ì¸ë””ì¼€ì´í„°ì™€ í•¨ê»˜ ì˜µì…˜ ë Œë”ë§
  const renderCustomOption = (option: StatusOption, isSelected: boolean) => {
    const colorClasses = getColorClasses(option.color);

    return (
      <div className="flex items-center justify-between w-full">
        <div className="flex items-center gap-2">
          {multiple && (
            <input
              type="checkbox"
              checked={isSelected}
              onChange={() => {}} // ë¶€ëª¨ì—ì„œ ì²˜ë¦¬
              className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
            />
          )}

          {showVisualIndicators && (
            <div className={`flex items-center gap-2 px-2 py-1 rounded-full ${colorClasses.bg}`}>
              <StatusIcon
                type={option.icon}
                className={`w-3 h-3 ${colorClasses.icon}`}
              />
              <span className={`text-xs font-medium ${colorClasses.text}`}>
                {option.label}
              </span>
            </div>
          )}

          {!showVisualIndicators && (
            <span>{option.label}</span>
          )}

          {option.description && (
            <span className="text-xs text-gray-500 dark:text-gray-400">
              {option.description}
            </span>
          )}
        </div>

        {showCount && option.count !== undefined && (
          <span className="text-xs text-gray-500 dark:text-gray-400">
            ({option.count})
          </span>
        )}
      </div>
    );
  };

  return (
    <div className={className}>
      <CategoryFilter
        options={options}
        value={value}
        onChange={onChange}
        label={label}
        placeholder={placeholder}
        multiple={multiple}
        disabled={disabled}
        showCount={false} // ì»¤ìŠ¤í…€ ë Œë”ë§ì—ì„œ ì²˜ë¦¬
