  function getStatusColor(status: string) {
    switch (status) {
      case 'low':
        return theme.colors[4]; // Red
      case 'normal':
        return theme.colors[1]; // Green
      case 'high':
        return theme.colors[0]; // Blue
      case 'overstock':
        return theme.colors[3]; // Orange
      default:
        return theme.colors[5]; // Gray
