/**
 * Dashboard data hooks for real-time data fetching
 * Handles auto-refresh, error handling, and state management
 */

import { useState, useEffect, useCallback, useRef } from 'react';

// Types for dashboard data
export interface DashboardStats {
  totalItems: number;
  activeCompanies: number;
  monthlyVolume: number;
  lowStockItems: number;
  volumeChange: number;
  trends: {
    items: number;
    companies: number;
    volume: number;
    lowStock: number;
  };
}

// Chart-specific data types (matching component interfaces)
export interface MonthlyTrendsData {
  month: string;
  date: Date;
  ì´ì¬ê³ ëŸ‰: number;
  ì…ê³ ëŸ‰: number;
  ì¶œê³ ëŸ‰: number;
  ìƒì‚°ëŸ‰: number;
  ì¬ê³ ê°€ì¹˜: number;
  íšŒì „ìœ¨: number;
}

export interface CategoryStockData {
  category: string;
  í˜„ì¬ê³ : number;
  ìµœì†Œì¬ê³ : number;
  ì•ˆì „ì¬ê³ : number;
  ìµœëŒ€ì¬ê³ : number;
  í’ˆëª©ìˆ˜: number;
  ì¬ê³ ê°€ì¹˜: number;
  íšŒì „ìœ¨: number;
  ë¶€ì¡±í’ˆëª©ìˆ˜: number;
  ê³¼ì¬ê³ í’ˆëª©ìˆ˜: number;
  ì¬ê³ ë¹„ìœ¨?: number;
}

export interface TransactionDistributionData {
  type: string;
  count: number;
  volume: number;
  value: number;
  percentage: number;
  items: number;
  avgPerTransaction: number;
  companies: number;
}

export interface TopItemData {
  item_id: string;
  item_name: string;
  item_code: string;
  category: string;
  currentStock: number;
  minimumStock?: number;
  safetyStock?: number;
  unitPrice: number;
  totalValue: number;
  monthlyVolume: number;
  turnoverRate: number;
  lastTransactionDate: Date | null;
  supplier: string | null;
  stockStatus: 'low' | 'normal' | 'high' | 'overstock';
  rank: number;
}

export interface ChartData {
  stocks: TopItemData[];
  transactions: TransactionDistributionData[];
  monthlyTrends: MonthlyTrendsData[];
  categoryStocks: CategoryStockData[];
}

export interface LowStockAlertData {
  item_id: string;
  item_name: string;
  item_code: string;
  category: string;
  currentStock: number;
  minimumStock: number;
  safetyStock: number;
  averageConsumption: number;
  stockoutRisk: number;
  daysUntilStockout: number;
  lastRestockDate: Date;
  supplier: string;
  leadTime: number;
  priority: 'critical' | 'high' | 'medium' | 'low';
  alertCreatedAt: Date;
  autoReorderEnabled: boolean;
  estimatedCost: number;
}

export interface AlertData {
  lowStockItems: LowStockAlertData[];
  recentTransactions: Array<{
    transaction_id: number;
    transaction_type: string;
    item_name: string;
    quantity: number;
    transaction_date: string;
    status: string;
  }>;
}

export interface DashboardData {
  stats: DashboardStats;
  charts: ChartData;
  alerts: AlertData;
  lastUpdated: Date;
}

// Refresh intervals in milliseconds
export const REFRESH_INTERVALS = {
  '30ì´ˆ': 30000,
  '1ë¶„': 60000,
  '5ë¶„': 300000,
  'ìˆ˜ë™': 0
} as const;

export type RefreshInterval = keyof typeof REFRESH_INTERVALS;

// Custom hook for dashboard data management
export const useDashboardData = (
  initialInterval: RefreshInterval = '1ë¶„',
  autoStart: boolean = true
) => {
  const [data, setData] = useState<DashboardData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [lastUpdated, setLastUpdated] = useState<Date | null>(null);
  const [refreshInterval, setRefreshInterval] = useState<RefreshInterval>(initialInterval);
  const [isAutoRefreshEnabled, setIsAutoRefreshEnabled] = useState(autoStart);
  const [retryCount, setRetryCount] = useState(0);

  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  // Fetch dashboard data
  const fetchDashboardData = useCallback(async (showLoading: boolean = true) => {
    try {
      if (showLoading) {
        setLoading(true);
      }
      setError(null);

      // Use single unified API endpoint with no-cache to prevent stale data
      const response = await fetch('/api/dashboard-simple', {
        cache: 'no-store',
        headers: {
          'Cache-Control': 'no-cache, no-store, must-revalidate',
          'Pragma': 'no-cache'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();

      // Validate response
      if (!result.success) {
        throw new Error(result.error || 'Failed to fetch dashboard data');
      }

      const newData: DashboardData = {
        stats: result.data.stats,
        charts: result.data.charts,
        alerts: result.data.alerts,
        lastUpdated: new Date()
      };

      setData(newData);
      setLastUpdated(new Date());
      setRetryCount(0);

    } catch (err: any) {
      console.error('Dashboard data fetch error:', err);
      setError(err.message || 'Failed to fetch dashboard data');
      setRetryCount(prev => prev + 1);
    } finally {
      if (showLoading) {
        setLoading(false);
      }
    }
  }, []);

  // Manual refresh function
  const refresh = useCallback(() => {
    fetchDashboardData(true);
  }, [fetchDashboardData]);

  // Auto-refresh with continuous intervals
  useEffect(() => {
    if (!isAutoRefreshEnabled || refreshInterval === 'ìˆ˜ë™') {
      return;
    }

    const interval = REFRESH_INTERVALS[refreshInterval];
    if (interval <= 0) return;

    // Use setInterval for continuous refreshing
    intervalRef.current = setInterval(() => {
      fetchDashboardData(false); // Silent refresh for auto-updates
    }, interval);

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, [fetchDashboardData, refreshInterval, isAutoRefreshEnabled]);

  // Initial data fetch
  useEffect(() => {
    if (typeof window !== 'undefined') {
      fetchDashboardData(true);
    }

    return () => {
      // Cleanup on unmount
      if (intervalRef.current) {
        clearTimeout(intervalRef.current);
      }
    };
  }, [fetchDashboardData]);

  // Pause auto-refresh when tab is not visible
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) {
        setIsAutoRefreshEnabled(false);
      } else {
        setIsAutoRefreshEnabled(true);
        // Refresh data when tab becomes visible again
        fetchDashboardData(false);
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [fetchDashboardData]);

  return {
    data,
    loading,
    error,
    lastUpdated,
    refreshInterval,
    isAutoRefreshEnabled,
    retryCount,
    setRefreshInterval,
    setIsAutoRefreshEnabled,
    refresh
  };
};

// Hook for real-time stats updates
export const useRealTimeStats = (refreshInterval: RefreshInterval = '1ë¶„') => {
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchStats = useCallback(async () => {
    try {
      setLoading(true);
