  grandTotal: number; // í•©ê³„ (ê³µê¸‰ê°€ì•¡ + ë¶€ê°€ì„¸)
  totalAmount: number; // ì›í™” í™˜ì‚° ê¸ˆì•¡
  currency: string;
  exchangeRate: number;
  taxRate: number;
}

export interface BulkTaxCalculationInput {
  items: Array<{
    quantity: number;
    unitPrice: number;
    taxRate?: number;
  }>;
  currency?: string;
  exchangeRate?: number;
  globalTaxRate?: number; // ì „ì²´ ì ìš© ì„¸ìœ¨
}

export interface BulkTaxCalculationResult {
  items: Array<{
    subtotalAmount: number;
    taxAmount: number;
    grandTotal: number;
  }>;
  totalSubtotal: number;
  totalTax: number;
  totalGrand: number;
  totalAmountKRW: number; // ì›í™” í™˜ì‚° ì´ì•¡
  currency: string;
  exchangeRate: number;
}

/**
 * ê¸°ë³¸ ë¶€ê°€ì„¸ìœ¨ (10%)
 */
export const DEFAULT_TAX_RATE = 10.00;

/**
 * ì§€ì› í†µí™” ëª©ë¡
 */
export const SUPPORTED_CURRENCIES = {
  KRW: 'ì›',
  USD: 'ë‹¬ëŸ¬',
  JPY: 'ì—”',
  CNY: 'ìœ„ì•ˆ',
  EUR: 'ìœ ë¡œ'
} as const;

/**
 * ë‹¨ì¼ í•­ëª© ë¶€ê°€ì„¸ ê³„ì‚°
 */
export function calculateTax(input: TaxCalculationInput): TaxCalculationResult {
  const {
    quantity,
    unitPrice,
    taxRate = DEFAULT_TAX_RATE,
    currency = 'KRW',
    exchangeRate = 1.0
  } = input;

  // ì†Œê³„ ê³„ì‚° (ìˆ˜ëŸ‰ Ã— ë‹¨ê°€)
  const subtotalAmount = Math.round(quantity * unitPrice * 100) / 100;

  // ë¶€ê°€ì„¸ ê³„ì‚°
  const taxAmount = Math.round(subtotalAmount * (taxRate / 100) * 100) / 100;

  // í•©ê³„ ê³„ì‚° (ì†Œê³„ + ë¶€ê°€ì„¸)
  const grandTotal = subtotalAmount + taxAmount;

  // ì›í™” í™˜ì‚° (ì™¸í™”ì¸ ê²½ìš°)
  const totalAmount = currency !== 'KRW' && exchangeRate > 0
    ? Math.round(grandTotal * exchangeRate * 100) / 100
    : grandTotal;

  return {
    subtotalAmount,
    taxAmount,
    grandTotal,
    totalAmount,
    currency,
    exchangeRate,
    taxRate
  };
}

/**
 * ë³µìˆ˜ í•­ëª© ë¶€ê°€ì„¸ ê³„ì‚°
 */
export function calculateBulkTax(input: BulkTaxCalculationInput): BulkTaxCalculationResult {
  const {
    items,
    currency = 'KRW',
    exchangeRate = 1.0,
    globalTaxRate
  } = input;

  let totalSubtotal = 0;
  let totalTax = 0;
  let totalGrand = 0;

  const calculatedItems = items.map(item => {
    const taxRate = globalTaxRate ?? item.taxRate ?? DEFAULT_TAX_RATE;

    // í•­ëª©ë³„ ê³„ì‚°
    const subtotalAmount = Math.round(item.quantity * item.unitPrice * 100) / 100;
    const taxAmount = Math.round(subtotalAmount * (taxRate / 100) * 100) / 100;
    const grandTotal = subtotalAmount + taxAmount;

    // ëˆ„ì  í•©ê³„
    totalSubtotal += subtotalAmount;
    totalTax += taxAmount;
    totalGrand += grandTotal;

    return {
      subtotalAmount,
      taxAmount,
      grandTotal
    };
  });

  // ì›í™” í™˜ì‚°
  const totalAmountKRW = currency !== 'KRW' && exchangeRate > 0
    ? Math.round(totalGrand * exchangeRate * 100) / 100
    : totalGrand;

  return {
    items: calculatedItems,
    totalSubtotal: Math.round(totalSubtotal * 100) / 100,
    totalTax: Math.round(totalTax * 100) / 100,
    totalGrand: Math.round(totalGrand * 100) / 100,
    totalAmountKRW,
    currency,
    exchangeRate
  };
}

/**
 * ë¶€ê°€ì„¸ ì—­ê³„ì‚° (ì´ì•¡ì—ì„œ ê³µê¸‰ê°€ì•¡ê³¼ ë¶€ê°€ì„¸ ë¶„ë¦¬)
 */
export function reverseTaxCalculation(
  totalAmount: number,
  taxRate: number = DEFAULT_TAX_RATE
): { subtotal: number; tax: number } {
  // ê³µê¸‰ê°€ì•¡ = ì´ì•¡ / (1 + ì„¸ìœ¨)
  const subtotal = Math.round(totalAmount / (1 + taxRate / 100) * 100) / 100;

  // ë¶€ê°€ì„¸ = ì´ì•¡ - ê³µê¸‰ê°€ì•¡
  const tax = Math.round((totalAmount - subtotal) * 100) / 100;

  return { subtotal, tax };
}

/**
 * ì„¸ê¸ˆê³„ì‚°ì„œ ë²ˆí˜¸ ìƒì„±
 * í˜•ì‹: YYYYMMDD-XXXX (ì¼ì-ìˆœë²ˆ)
 */
export function generateTaxInvoiceNumber(date: Date = new Date(), sequence: number = 1): string {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  const seq = String(sequence).padStart(4, '0');

  return `${year}${month}${day}-${seq}`;
}

/**
 * ê¸ˆì•¡ì„ í•œêµ­ ì›í™” í˜•ì‹ìœ¼ë¡œ í¬ë§·íŒ…
 */
export function formatKRW(amount: number): string {
  return new Intl.NumberFormat('ko-KR', {
    style: 'currency',
    currency: 'KRW',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  }).format(amount);
}

/**
 * ì™¸í™”ë¥¼ í•´ë‹¹ í†µí™” í˜•ì‹ìœ¼ë¡œ í¬ë§·íŒ…
 */
export function formatCurrency(amount: number, currency: string = 'KRW'): string {
  const locale = currency === 'KRW' ? 'ko-KR' :
                 currency === 'USD' ? 'en-US' :
                 currency === 'JPY' ? 'ja-JP' :
                 currency === 'CNY' ? 'zh-CN' :
                 currency === 'EUR' ? 'de-DE' : 'en-US';

  const decimals = currency === 'KRW' || currency === 'JPY' ? 0 : 2;

  return new Intl.NumberFormat(locale, {
    style: 'currency',
    currency: currency,
    minimumFractionDigits: decimals,
    maximumFractionDigits: decimals
  }).format(amount);
}

/**
 * ë¶€ê°€ì„¸ í¬í•¨/ë¯¸í¬í•¨ ì—¬ë¶€ í™•ì¸
 */
export function isTaxIncluded(
  totalAmount: number,
  subtotalAmount: number,
  taxRate: number = DEFAULT_TAX_RATE
): boolean {
  const expectedTax = subtotalAmount * (taxRate / 100);
  const expectedTotal = subtotalAmount + expectedTax;

  // 1ì› ë‹¨ìœ„ ì˜¤ì°¨ í—ˆìš©
  return Math.abs(totalAmount - expectedTotal) < 1;
}

/**
 * ì„¸ê¸ˆê³„ì‚°ì„œ ë°œí–‰ ê°€ëŠ¥ ì—¬ë¶€ ì²´í¬
 */
export function canIssueTaxInvoice(data: {
  supplierBusinessNumber?: string;
  buyerBusinessNumber?: string;
  amount: number;
}): { canIssue: boolean; reason?: string } {
  // ì‚¬ì—…ìë²ˆí˜¸ ì²´í¬
  if (!data.supplierBusinessNumber) {
    return { canIssue: false, reason: 'ê³µê¸‰ì ì‚¬ì—…ìë²ˆí˜¸ê°€ ì—†ìŠµë‹ˆë‹¤.' };
  }

  if (!data.buyerBusinessNumber) {
    return { canIssue: false, reason: 'ê³µê¸‰ë°›ëŠ”ì ì‚¬ì—…ìë²ˆí˜¸ê°€ ì—†ìŠµë‹ˆë‹¤.' };
  }

  // ê¸ˆì•¡ ì²´í¬
  if (data.amount <= 0) {
    return { canIssue: false, reason: 'ë°œí–‰ ê¸ˆì•¡ì´ 0ì› ì´í•˜ì…ë‹ˆë‹¤.' };
  }

  return { canIssue: true };
}

/**
 * ì‚¬ì—…ìë²ˆí˜¸ ìœ íš¨ì„± ê²€ì¦ (ê°„ë‹¨í•œ í˜•ì‹ ì²´í¬)
 */
export function validateBusinessNumber(businessNumber: string): boolean {
  // í•˜ì´í”ˆ ì œê±°
  const cleaned = businessNumber.replace(/-/g, '');

  // 10ìë¦¬ ìˆ«ìì¸ì§€ í™•ì¸
  if (!/^\d{10}$/.test(cleaned)) {
    return false;
  }

  // TODO: ì‹¤ì œ ì‚¬ì—…ìë²ˆí˜¸ ê²€ì¦ ì•Œê³ ë¦¬ì¦˜ êµ¬í˜„
  // ì—¬ê¸°ì„œëŠ” í˜•ì‹ë§Œ ì²´í¬

  return true;
}

/**
 * ì„¸ìœ¨ ì˜µì…˜ ëª©ë¡
 */
export const TAX_RATE_OPTIONS = [
  { value: 0, label: 'ì˜ì„¸ìœ¨ (0%)' },
  { value: 10, label: 'ì¼ë°˜ê³¼ì„¸ (10%)' },
  { value: -1, label: 'ë©´ì„¸' }
];

