import { NextRequest, NextResponse } from 'next/server';
import { getSupabaseClient } from '@/lib/db-unified';
import { nextSerial, getTransactionPrefix } from '@/lib/serial';
import { logger, logApiCall, logBusinessEvent } from '@/lib/logger';

// GET: ì¬ê³  ì´ë™ ëª©ë¡ ì¡°íšŒ
export async function GET(request: NextRequest) {
  const startTime = Date.now();
  const correlationId = request.headers.get('x-correlation-id') ||
                       `inventory_transactions_get_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  logger.setCorrelationId(correlationId);

  try {
    logger.debug('ì¬ê³  ì´ë™ ëª©ë¡ ì¡°íšŒ ìš”ì²­ ì‹œì‘');
    const searchParams = request.nextUrl.searchParams;
    const search = searchParams.get('search');
    const startDate = searchParams.get('start_date');
    const endDate = searchParams.get('end_date');
    const warehouse = searchParams.get('warehouse');

    const supabase = getSupabaseClient();

    // Build safe query using Supabase client
    let query = supabase
      .from('inventory_transactions')
      .select(`
        transaction_id,
        transaction_date,
        item_id,
        quantity,
        notes,
        document_number,
        status,
        transaction_type,
        items!inner(item_code, item_name, unit)
      `)
      .in('transaction_type', ['ì…ê³ ', 'ì¶œê³ ', 'ìƒì‚°ì…ê³ ', 'ìƒì‚°ì¶œê³ ', 'ì´ë™', 'ì¡°ì •', 'íê¸°', 'ì¬ê³ ì¡°ì •']);

    // Apply filters safely
    if (startDate) {
      query = query.gte('transaction_date', startDate);
    }

    if (endDate) {
      query = query.lte('transaction_date', endDate);
    }

    if (warehouse) {
      query = query.eq('warehouse_id', parseInt(warehouse, 10));
    }

    if (search) {
      query = query.or(`items.item_code.ilike.%${search}%,items.item_name.ilike.%${search}%`);
    }

    // Apply ordering and limit
    query = query
      .order('transaction_date', { ascending: false })
      .limit(100);

    const { data: transactions, error } = await query;

    if (error) {
      throw new Error(`Database query failed: ${error.message}`);
    }

    // Transform data to match expected format
    const formattedTransfers = transactions?.map((t: any) => ({
      id: t.transaction_id,
      transfer_date: t.transaction_date,
      item_id: t.item_id,
      item_code: t.items?.item_code,
      item_name: t.items?.item_name,
      quantity: t.quantity,
      unit: t.items?.unit,
      notes: t.notes,
      document_number: t.document_number,
      document_status: t.status,
      transaction_type: t.transaction_type,
      from_warehouse: 'Main',
      to_warehouse: 'Sub'
    })) || [];

    const responseTime = Date.now() - startTime;
    logApiCall('GET', '/api/inventory/transactions', 200, responseTime);
    logger.info(`ì¬ê³  ì´ë™ ëª©ë¡ ì¡°íšŒ ì™„ë£Œ: ${formattedTransfers.length}ê°œ ì´ë™ë‚´ì—­`, {
      transferCount: formattedTransfers.length,
      filters: { search, startDate, endDate, warehouse }
    });

    return NextResponse.json({
      success: true,
      data: formattedTransfers,
      meta: {
        total: formattedTransfers.length,
        responseTime
      }
    });

  } catch (error) {
    const responseTime = Date.now() - startTime;
    logApiCall('GET', '/api/inventory/transactions', 500, responseTime);
    logger.error('ì¬ê³  ì´ë™ ëª©ë¡ ì¡°íšŒ ì‹¤íŒ¨', error as Error);

    return NextResponse.json({
      success: false,
      error: error instanceof Error ? error.message : 'Failed to fetch inventory transactions'
    }, { status: 500 });
  }
}

// POST: ì¬ê³  ì´ë™ ë“±ë¡
export async function POST(request: NextRequest) {
  const startTime = Date.now();
  const correlationId = request.headers.get('x-correlation-id') ||
                       `inventory_transactions_post_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  logger.setCorrelationId(correlationId);

  try {
    logger.debug('ì¬ê³  ì´ë™ ë“±ë¡ ìš”ì²­ ì‹œì‘');

    // SECURITY FIX: Use request.text() + JSON.parse() for proper Korean character handling
    const text = await request.text();
    const body = JSON.parse(text);

    const {
      item_id,
      quantity,
      transaction_type,
      from_warehouse_id,
      to_warehouse_id,
      notes,
      transfer_date
    } = body;

    // Validate required fields
    if (!item_id || !quantity || !transaction_type) {
      return NextResponse.json({
        success: false,
        error: 'item_id, quantity, and transaction_type are required'
      }, { status: 400 });
    }

    if (quantity <= 0) {
      return NextResponse.json({
        success: false,
        error: 'quantity must be greater than 0'
      }, { status: 400 });
    }

    // Validate transaction type
    const validTypes = ['ì…ê³ ', 'ì¶œê³ ', 'ìƒì‚°ì…ê³ ', 'ìƒì‚°ì¶œê³ ', 'ì´ë™', 'ì¡°ì •', 'íê¸°', 'ì¬ê³ ì¡°ì •'];
    if (!validTypes.includes(transaction_type)) {
      return NextResponse.json({
        success: false,
        error: `Invalid transaction_type. Must be one of: ${validTypes.join(', ')}`
      }, { status: 400 });
    }

    const supabase = getSupabaseClient();

    // Get transaction prefix and generate document number
    const prefix = getTransactionPrefix(transaction_type);
    const documentNumber = await nextSerial(prefix);

    // For ì¶œê³  types, check stock availability first
    if (['ì¶œê³ ', 'ìƒì‚°ì¶œê³ ', 'íê¸°'].includes(transaction_type)) {
      // SECURITY FIX: Check stock using Supabase client
      const { data: item, error: stockError } = await supabase
        .from('items')
        .select('current_stock')
        .eq('item_id', item_id)
        .single();

      if (stockError || !item) {
        return NextResponse.json({
          success: false,
          error: 'í’ˆëª©ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤'
        }, { status: 404 });
      }

      const currentStock = item.current_stock || 0;
      if (currentStock < quantity) {
        return NextResponse.json({
          success: false,
          error: `ì¬ê³ ê°€ ë¶€ì¡±í•©ë‹ˆë‹¤. í˜„ì¬ ì¬ê³ : ${currentStock}, ìš”ì²­ ìˆ˜ëŸ‰: ${quantity}`
        }, { status: 400 });
      }
    }

    // SECURITY FIX: Create inventory transaction using Supabase client
    const { data: createData, error: createError } = await supabase
      .from('inventory_transactions')
      .insert({
        transaction_date: transfer_date || new Date().toISOString().split('T')[0],
        transaction_type: transaction_type,
        item_id: item_id,
        quantity: quantity,
        warehouse_id: from_warehouse_id || null,
        notes: notes || '',
        document_number: documentNumber,
        status: 'ì™„ë£Œ',
        created_at: new Date().toISOString()
      })
      .select('transaction_id')
      .single();

    if (createError || !createData) {
      console.error('Transaction creation error:', createError);
      return NextResponse.json({
        success: false,
        error: 'ê±°ë˜ ìƒì„±ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤'
      }, { status: 500 });
    }

