        );

        if (!expiryValidation.isValid) {
          throw new Error(`Expiry date validation failed: ${expiryValidation.errors.join(', ')}`);
        }

        if (expiryValidation.warnings.length > 0) {
          console.warn('Expiry date warnings:', expiryValidation.warnings);
